mapper.select:
select username, nickname, avatar, high_score, game_data, @row := @row + 1 as sequence
from (select @row := 0) as rowtemptable,
	 (select * from `player` order by high_score desc limit 10) as playerinfo

select username, nickname, avatar, high_score, game_data, @row := @row + 1 as sequence
from (select @row := 0) as rowtemptable,
	 (select * from `player` order by high_score desc limit 10) as playerinfo
where username = #{username}
